import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:5000";

export default function FirewallManager() {
  const [port, setPort] = useState("");
  const [action, setAction] = useState("ACCEPT");
  const [rules, setRules] = useState([]);

  const fetchRules = async () => {
    const res = await fetch(`${API_URL}/list_rules`);
    const data = await res.json();
    setRules(data.rules);
  };

  const addRule = async () => {
    await fetch(`${API_URL}/add_rule`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ port, action }),
    });
    setPort("");
    fetchRules();
  };

  const deleteRule = async (port) => {
    await fetch(`${API_URL}/delete_rule`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ port }),
    });
    fetchRules();
  };

  useEffect(() => {
    fetchRules();
  }, []);

  return (
    <div>
      <h3>Manage Firewall Rules</h3>
      <input
        type="text"
        placeholder="Port number"
        value={port}
        onChange={(e) => setPort(e.target.value)}
      />
      <select value={action} onChange={(e) => setAction(e.target.value)}>
        <option value="ACCEPT">Allow</option>
        <option value="DROP">Block</option>
      </select>
      <button onClick={addRule}>Add Rule</button>

      <h4>Current Rules:</h4>
      <ul>
        {rules.map((r, i) => (
          <li key={i}>
            Port: {r.port} | Action: {r.action}{" "}
            <button onClick={() => deleteRule(r.port)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
